package io.Jhonyrod.HueRuler

import android.util.Base64
import java.io.ByteArrayInputStream as BAIS
import java.security.KeyStore
import java.security.cert.*
import java.security.cert.X509Certificate as X509C

private const val curr = ""
private const val next = ""

val keyStore:KeyStore by lazy {
	KeyStore.getInstance(KeyStore.getDefaultType())
	.apply {
		load(null, null)
		setCertificateEntry("Current", der2x509(curr))
		setCertificateEntry("Next", der2x509(next))
	}
}

fun der2x509(der:String):X509C = CertificateFactory
.getInstance("X.509")
.generateCertificate(BAIS(
	Base64.decode(der, Base64.DEFAULT)
)) as X509C


//WARNING:	Fragile..? if you know how to use
//			LDAP instead go right ahead.
fun subCN(c:X509C):String=c.subjectX500Principal.name
.substringAfter("CN=", "").substringBefore(",")